import React, { useState } from 'react'
import axios from "axios";
import {apiCommentCreate} from './lookup'


export function CommentCreate(props){
  const [content, setcontent] = useState('');
  const [image, setimage] = useState('');
  

  const handleImageChange = e => {
      setimage(e.target.files[0]);
           };

  const  handleChange  = e => {
    setcontent  (e.target.value);
          };

    const handleSubmit = (event) => {
      event.preventDefault()
      let form_data = new FormData();
      form_data.append('image', image);
      form_data.append('content', content);
      let url = apiCommentCreate()
      axios.post(url, form_data, {
      headers: {
        'content-type': 'multipart/form-data'
      }
        })
        .then(res => {
          console.log(res.data);
        })
        .catch(err => console.log(err))
    };
    return <div className={props.className}>
          <form onSubmit={handleSubmit}>
            <input type="text" placeholder='Content' id='content' value={content} onChange={handleChange} required/>

            

            <p>
           <input type="file"
                   id="image"
                   accept="image/png, image/jpeg"  onChange={handleImageChange} required/>

              </p>     
            <button type='submit' className='btn btn-primary my-3'>Comment</button>
        </form>
  </div>
}
