import React from 'react'
import axios from "axios";
import {apiCommentCreate} from './lookup'


export function CommentCreate(props){
  const [Content, setContent]= React.useState();
  const [Image, setImage]= React.useState();
  
    const handleSubmit = (event) => {
      event.preventDefault()
      const form_data = new FormData();
      form_data.append("image", Image);
      form_data.append("content", Content);
      
      let url = 'http://localhost:8000/api/tweets/commentcreate/';
      axios
      .post(url, form_data, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      })
      .then((response) => {
        // successfully uploaded response
      })
      .catch((error) => {
        // error response
      });
    }
    return <div className={props.className}>
          <form onSubmit={handleSubmit}>
            <input type="text" placeholder='Content' id='content' onChange={(e) => setContent(e.target.value)} />
            <input type="file" id="image" accept="image/png, image/jpeg" onChange={(e) => setImage(e.target.files)} />
            <button type='submit' className='btn btn-primary my-3'>Comment</button>
        </form>
  </div>
}
